this page use router,scss,axios

<template>
  <div>
    <div class="top"></div>
    <div class="main">
      Form đăng nhập (vuelidate)
      <div class="form-group">
        <input
          type="text"
          placeholder="Tài Khoản"
          v-model.trim="$v.name.$model"
        />

        <div class="validate">
          <div class="error" v-if="!$v.name.required">username is required</div>
          <div class="error" v-if="!$v.name.minLength">
            username must have at least
            {{ $v.name.$params.minLength.min }} letters.
          </div>
        </div>
      </div>
      <div class="form-group">
        <input
          type="password"
          placeholder="Mật Khẩu"
          v-model.trim="$v.pass.$model"
        />
        <div class="validate">
          <div class="error" v-if="!$v.pass.required">password is required</div>
          <div class="error" v-if="!$v.pass.minLength">
            password must have at least
            {{ $v.pass.$params.minLength.min }} letters.
          </div>
        </div>
      </div>

      <button class="submit" @click="validate()">Đăng Nhập</button>
      <spinner class="spinner" v-if="this.$store.state.is_login" />
      <div class="route">
        <router-link to="vee">Đăng nhập với vee_validate</router-link>
        <router-link to="vuetify">Đăng nhập với vuetify_validate</router-link>
      </div>
    </div>
    <div class="bot"></div>
  </div>
</template>

<script>
import spinner from "./spinner";
import Schema from "async-validator";
export default {
  components: { spinner },
  methods: {
    submit() {
      this.$store.dispatch("usernameLogin", {
        name: this.name,
        pass: this.pass,
      });
      if (this.$store.state.is_login === true) {
        setTimeout(() => {
          this.$router.push("/user");
        }, 1500);
      }
    },
    validate() {
      const descriptor = {
        name: {
          type: "string",
          required: true,
          validator: (rule, value) => value === "muji",
        },
        age: {
          type: "number",
          asyncValidator: (rule, value) => {
            return new Promise((resolve, reject) => {
              if (value < 18) {
                reject("too young"); // reject with error message
              } else {
                resolve();
              }
            });
          },
        },
      };
      const validator = new Schema(descriptor);
      validator.validate({ name: "muji" }, (errors, fields) => {
        if (errors) {
          // validation failed, errors is an array of all errors
          // fields is an object keyed by field name with an array of
          // errors per field
          console.log(errors,fields);
        }
        // validation passed
      });

      // PROMISE USAGE
      validator
        .validate({ name: "muji", age: 16 })
        .then(() => {
          // validation passed or without error message
        })
        .catch(({ errors, fields }) => {
          return console.log(errors, fields);
        });
    },
  },
  data() {
    return {
      name: "",
      pass: "",
    };
  },
};
</script>

<style lang='scss' src='../style.scss' scoped/>
