this page use router,scss,axios

<template>
  <div>
    <div class="top"></div>
    <div class="main">
      Form đăng nhập (vuelidate)
      <div class="form-group">
        <input
          type="text"
          placeholder="Tài Khoản"
          v-model.trim="$v.name.$model"
          @keyup="nowarn()"
        />
        <div class="validate">
          <div class="error" v-if="!$v.name.required">username is required</div>
          <div class="error" v-if="!$v.name.minLength">
            username must have at least
            {{ $v.name.$params.minLength.min }} letters.
          </div>
        </div>
      </div>
      <div class="form-group">
        <input
          type="password"
          placeholder="Mật Khẩu"
          v-model.trim="$v.pass.$model"
          @keyup="nowarn()"
        />
        <div class="validate">
          <div class="error" v-if="!$v.pass.required">password is required</div>
          <div class="error" v-if="!$v.pass.minLength">
            password must have at least
            {{ $v.pass.$params.minLength.min }} letters.
          </div>
        </div>
      </div>

      <button class="submit" @click="submit()">Đăng Nhập</button>
    </div>
    <div class="bot"></div>
  </div>
</template>

<script>
import { required, minLength } from "vuelidate/lib/validators";
export default {
  methods: {
    submit() {
      if (!this.pass || !this.name) {
        this.warn = true;
      } else {
        const account = this.acc.filter(
          (element) =>
            element.username === this.name && element.pass === this.pass
        );
        if (account.length !== 0) {
          this.fail = false;
          this.acc_present = account[0];
          setTimeout(() => {
            this.$router.push("/user");
            localStorage.setItem("dataUser", JSON.stringify(this.acc_present));
          }, 1000);
        } else {
          this.fail = true;
        }
      }
    },
    nowarn() {
      this.warn = false;
    },
  },
  data() {
    return {
      name: "",
      pass: "",
      fail: false,
      warn: false,
      acc_present: "",
      acc: [
        {
          id: 1,
          username: "user1",
          pass: "123",
          fullname: "Nguyễn Văn A",
          birthday: " 08 03 2001",
          email: "a@gmail.com",
          role: "Admin",
        },
        {
          id: 2,
          username: "user2",
          pass: "123",
          fullname: "Nguyễn Văn B",
          birthday: "Mar 04 2000",
          email: "b@gmail.com",
          role: "User",
        },
      ],
    };
  },
  validations: {
    name: {
      required,
      minLength: minLength(4),
    },
    pass: {
      required,
      minLength: minLength(3),
    },
  },
};
</script>

<style lang='scss' src='../style.scss' scoped/>
